class Solution(object):
    def findNumberOfLIS(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        if len(nums) < 2:
            return len(nums)

        # test for special case: monotonically increasing
        mono_incr = True
        for i in xrange(len(nums)-1):
            if nums[i] >= nums[i+1]:
                mono_incr = False
                break
        if mono_incr:
            return 1

        # test for special case: no increasing, so all LIS has length 1
        no_incr = True
        for i in xrange(len(nums)-1):
            if nums[i] < nums[i+1]:
                no_incr = False
                break
        if no_incr:
            return len(nums)

        # test for special case: staircase scenario, only 2 values, "small" and "large", and all "small" are before all "large"
        d_num = {}
        for num in nums:
            if d_num.get(num) is None:
                d_num[num] = 1
            else:
                d_num[num] += 1
        no_decr = True
        for i in xrange(len(nums)-1):
            if nums[i] > nums[i+1]:
                no_decr = False
                break
        if len(d_num.keys()) == 2 and no_decr:
            return d_num.values()[0]*d_num.values()[1]

        self.d = {}
        self.d_len = {}
        LIS_len, count = self.findLIS(nums)
        return count

    def findLIS(self, nums):
        import sys
        r_lim = sys.getrecursionlimit()
        sys.setrecursionlimit(2000)
        res = self.findLISRecursive([], nums)
        sys.setrecursionlimit(r_lim)
        return res

    def findLISRecursive(self, left, nums):
        if len(nums) == 0:
            return len(left), 1
        id_first_larger = 0
        if len(left) > 0:
            while id_first_larger < len(nums):
                if nums[id_first_larger] > left[-1]:
                    break
                id_first_larger += 1

        # when hashing, note that when we do the recursion, we are using nums[id_first_larger+1:], which does not change
        # as long as id_first_larger is the same, even when nums[:id_first_larger] is changing
        # so we only need to pass the number of elements counted from back of nums to encode the hash
        h = self.getHash(len(nums) - id_first_larger, left)
        if self.d.get(h) is not None:
            LIS_len, count = self.d[h]
            return len(left)+LIS_len, count
        LIS_len_exclude, count = self.findLISRecursive(left, nums[id_first_larger+1:])

        if len(left) == 0 or id_first_larger < len(nums):
            LIS_len_include, count_include = self.findLISRecursive(left+[nums[id_first_larger]], nums[id_first_larger+1:])
            LIS_len = max(LIS_len_exclude, LIS_len_include)
            if LIS_len_exclude == LIS_len_include:
                count = count + count_include
            elif LIS_len_include > LIS_len_exclude:
                count = count_include
        else:
            LIS_len = LIS_len_exclude
        self.d[h] = LIS_len-len(left), count
        return LIS_len, count

    def getHash(self, id_first_larger_from_back, left):
        h = ''
        if len(left) > 0:
            h += str(left[-1])
        h += ';'
        h += str(id_first_larger_from_back)
        return h

